    74→                message.css_class == 'image' %}🖼️ {% endif %}{{ message.display_type }}</span>
    75→            <div style='display: flex; flex-direction: column; align-items: flex-end; gap: 2px;'>
    76→                <span class='timestamp'>{{ message.formatted_timestamp }}</span>
    77→                {% if message.token_usage %}
    78→                <span class='token-usage' style='font-size: 0.75em; color: #888;'>{{ message.token_usage }}</span>
    79→                {% endif %}
    80→            </div>
    81→        </div>
    82→        <div class='content'>{{ message.content_html | safe }}</div>
    83→    </div>
    84→    {% endif %}
    85→    {% endfor %}
    86→
    87→    <button class="timeline-toggle floating-btn" id="toggleTimeline" title="Show timeline">📆</button>
    88→    <button class="filter-messages floating-btn" id="filterMessages" title="Toggle filters">🔍</button>
    89→    <button class="toggle-details floating-btn" id="toggleDetails" title="Toggle all details">📋</button>
    90→    <a class="scroll-top floating-btn" title="Scroll to top" href="#title">🔝</a>
    91→
    92→    <script>
    93→        document.addEventListener('DOMContentLoaded', function () {
    94→            const toggleButton = document.getElementById('toggleDetails');
    95→            const timelineButton = document.getElementById('toggleTimeline');
    96→            const filterButton = document.getElementById('filterMessages');
    97→            const filterToolbar = document.querySelector('.filter-toolbar');
    98→            const selectAllButton = document.getElementById('selectAll');
    99→            const selectNoneButton = document.getElementById('selectNone');
   100→            const closeFiltersButton = document.getElementById('closeFilters');
   101→            const filterToggles = document.querySelectorAll('.filter-toggle');
   102→
   103→            // Timeline toggle functionality
   104→            if (timelineButton) {
   105→                timelineButton.addEventListener('click', function () {
   106→                    if (window.toggleTimeline) {
   107→                        window.toggleTimeline();
   108→                    }
   109→                });
   110→            }
   111→
   112→            // Toggle details functionality
   113→            function updateToggleButton() {
   114→                const allDetails = document.querySelectorAll('details.collapsible-details');
   115→                const openCount = document.querySelectorAll('details[open].collapsible-details').length;
   116→                const totalCount = allDetails.length;
   117→
   118→                if (totalCount === 0) {
   119→                    toggleButton.style.display = 'none';
   120→                    return;
   121→                }
   122→
   123→                // If more than half are open, show "close all" state, otherwise show "open all"
   124→                const mostlyOpen = openCount > totalCount / 2;
   125→                toggleButton.textContent = mostlyOpen ? '📦' : '🗃️';
   126→                toggleButton.title = mostlyOpen ? 'Close all details' : 'Open all details';
   127→            }
   128→
   129→            function toggleAllDetails() {
   130→                const allDetails = document.querySelectorAll('details.collapsible-details');
   131→                const openCount = document.querySelectorAll('details[open].collapsible-details').length;
   132→                const shouldOpen = openCount <= allDetails.length / 2;
   133→
   134→                allDetails.forEach(details => {
   135→                    if (shouldOpen) {
   136→                        details.setAttribute('open', '');
   137→                    } else {
   138→                        details.removeAttribute('open');
   139→                    }
   140→                });
   141→
   142→                updateToggleButton();
   143→            }
   144→
   145→            toggleButton.addEventListener('click', toggleAllDetails);
   146→
   147→            // Filter toolbar toggle functionality
   148→            function toggleFilterToolbar() {
   149→                const isVisible = filterToolbar.classList.contains('visible');
   150→                if (isVisible) {
   151→                    filterToolbar.classList.remove('visible');
   152→                    filterButton.classList.remove('active');
   153→                    filterButton.title = 'Show filters';
   154→                } else {
   155→                    filterToolbar.classList.add('visible');
   156→                    filterButton.classList.add('active');
   157→                    filterButton.title = 'Hide filters';
   158→                }
   159→            }
   160→
   161→            filterButton.addEventListener('click', toggleFilterToolbar);
   162→            closeFiltersButton.addEventListener('click', toggleFilterToolbar);
   163→
   164→            // Count messages by type and update button labels
   165→            function updateMessageCounts() {
   166→                const messageTypes = ['user', 'assistant', 'sidechain', 'system', 'tool_use', 'tool_result', 'thinking', 'image'];
   167→
   168→                messageTypes.forEach(type => {
   169→                    const messages = document.querySelectorAll(`.message.${type}:not(.session-header)`);
   170→                    const count = messages.length;
   171→                    const toggle = document.querySelector(`[data-type="${type}"]`);
   172→                    const countSpan = toggle.querySelector('.count');
   173→
   174→                    if (countSpan) {
   175→                        countSpan.textContent = `(${count})`;
   176→
   177→                        // Hide toggles for message types with 0 count
   178→                        if (count === 0) {
   179→                            toggle.style.display = 'none';
   180→                        } else {
   181→                            toggle.style.display = 'flex';
   182→                        }
   183→                    }
   184→                });
   185→            }
   186→
   187→            // Filter functionality
   188→            function applyFilter() {
   189→                const activeTypes = Array.from(filterToggles)
   190→                    .filter(toggle => toggle.classList.contains('active'))
   191→                    .map(toggle => toggle.dataset.type);
   192→
   193→                // Show/hide messages based on active toggle buttons
   194→                const allMessages = document.querySelectorAll('.message:not(.session-header)');
   195→                allMessages.forEach(message => {
   196→                    let shouldShow = false;
   197→                    
   198→                    // Special handling for sidechain messages
   199→                    if (message.classList.contains('sidechain')) {
   200→                        // For sidechain messages, show if both sidechain filter is active AND their message type filter is active
   201→                        const sidechainActive = activeTypes.includes('sidechain');
   202→                        const messageTypeActive = activeTypes.some(type => 
   203→                            type !== 'sidechain' && message.classList.contains(type)
   204→                        );
   205→                        shouldShow = sidechainActive && messageTypeActive;
   206→                    } else {
   207→                        // For non-sidechain messages, show if any of their types are active
   208→                        shouldShow = activeTypes.some(type => message.classList.contains(type));
   209→                    }
   210→                    
   211→                    if (shouldShow) {
   212→                        message.classList.remove('filtered-hidden');
   213→                    } else {
   214→                        message.classList.add('filtered-hidden');
   215→                    }
   216→                });
   217→
   218→                // Update visible counts in real-time
   219→                updateVisibleCounts();
   220→
   221→                // Update filter button appearance based on whether all types are selected
   222→                const allTypesSelected = activeTypes.length === filterToggles.length;
   223→                if (!allTypesSelected && filterToolbar.classList.contains('visible')) {
   224→                    filterButton.classList.add('active');
   225→                } else if (allTypesSelected && filterToolbar.classList.contains('visible')) {
   226→                    filterButton.classList.add('active');
   227→                }
   228→            }
   229→
   230→            function updateVisibleCounts() {
   231→                const messageTypes = ['user', 'assistant', 'sidechain', 'system', 'tool_use', 'tool_result', 'thinking', 'image'];
   232→
   233→                messageTypes.forEach(type => {
   234→                    const visibleMessages = document.querySelectorAll(`.message.${type}:not(.session-header):not(.filtered-hidden)`);
   235→                    const totalMessages = document.querySelectorAll(`.message.${type}:not(.session-header)`);
   236→                    const visibleCount = visibleMessages.length;
   237→                    const totalCount = totalMessages.length;
   238→
   239→                    const toggle = document.querySelector(`[data-type="${type}"]`);
   240→                    const countSpan = toggle.querySelector('.count');
   241→
   242→                    if (countSpan && totalCount > 0) {
   243→                        // Show "visible/total" format when filtering is active
   244→                        const activeTypes = Array.from(filterToggles)
   245→                            .filter(toggle => toggle.classList.contains('active'))
   246→                            .map(toggle => toggle.dataset.type);
   247→
   248→                        const isFiltering = activeTypes.length < filterToggles.length;
   249→
   250→                        if (isFiltering && visibleCount !== totalCount) {
   251→                            countSpan.textContent = `(${visibleCount}/${totalCount})`;
   252→                        } else {
   253→                            countSpan.textContent = `(${totalCount})`;
   254→                        }
   255→                    }
   256→                });
   257→            }
   258→
   259→            function toggleFilter(button) {
   260→                button.classList.toggle('active');
   261→                applyFilter();
   262→            }
   263→
   264→            function selectAllTypes() {
   265→                filterToggles.forEach(toggle => {
   266→                    toggle.classList.add('active');
   267→                });
   268→                applyFilter();
   269→            }
   270→
   271→            function selectNoTypes() {
   272→                filterToggles.forEach(toggle => {
   273→                    toggle.classList.remove('active');
   274→                });
   275→                applyFilter();
   276→            }
   277→
   278→            // Event listeners for filter toggles
   279→            filterToggles.forEach(toggle => {
   280→                toggle.addEventListener('click', () => toggleFilter(toggle));
   281→            });
   282→
   283→            selectAllButton.addEventListener('click', selectAllTypes);
   284→            selectNoneButton.addEventListener('click', selectNoTypes);
   285→
   286→            // Initialize button state and message counts
   287→            updateToggleButton();
   288→            updateMessageCounts();
   289→        });
   290→    </script>
   291→</body>
   292→
   293→</html>

